'use strict';

const context = require('../context');
const onHeaders = require('on-headers');
const onFinished = require('on-finished');
const logger = require('../logger');
const Ajv = require('ajv');
const ajv = new Ajv();
const userJsonSchema = require('../../json-schema/user-schema');
const validate = ajv.compile(userJsonSchema);

class Interceptor {
  static accessLogging(req, res, next) {
    onHeaders(res, () => {
      context.set('startAt', process.hrtime());
    });
    onFinished(res, () => {
      if (
        // healthcheck 200 OK の場合はログ出力しない
        !(
          req.originalUrl.match(/healthcheck/) &&
          Math.floor(res.statusCode / 200) == 1
        )
      ) {
        const startAt = context.get('startAt');
        const diff = process.hrtime(startAt);
        const responseTime = diff[0] * 1e9 + diff[1]; // ns
        // アクセスログ出力
        logger.info(`${req.method} ${req.originalUrl}`, {
          req: {
            headers: req.headers,
            query: req.query,
          },
          res: {
            status: res.statusCode,
            responseTime: responseTime,
          },
        });
      }
    });
    next();
  }

  static validateUserJson(data) {
    return new Promise((resolve, reject) => {
      const result = validate(data); // true of false
      if (result) {
        resolve(result);
      } else {
        reject(validate.errors);
      }
    });
  }
}

module.exports = Interceptor;

'use strict';

const express = require('express');
const router = express.Router();
const {body, validationResult} = require('express-validator');
const boom = require('boom');
const logger = require('../logger');
const Interceptor = require('./interceptor');
const userUC = require('../usecase/user-uc');
const tokenUC = require('../usecase/token-uc');
const pdfUC = require('../usecase/view-uc');
const echoUC = require('../usecase/echo-uc');

/////////////////////
// access logging
/////////////////////

router.use(Interceptor.accessLogging);

/////////////////////
// application logics
/////////////////////

router.get('/echo', (req, res) => {
  const result = echoUC.echo();
  res.status(200).json(result);
});

router.post(
  '/auth',
  [
    body('id')
      .isString()
      .isLength({min: 1}),
    body('password')
      .isString()
      .isLength({min: 1}),
  ],
  async (req, res, next) => {
    // validator
    const errors = await validationResult(req);
    if (!errors.isEmpty()) {
      logger.warn(errors.array());
      next(boom.badRequest(errors.array()));
    } else {
      const id = req.body.id;
      const password = req.body.password;
      // コンテンツネゴシエーション（アクセプトヘッダを見て分岐）
      res.format({
        'application/json': () => {
          res.json({token: tokenUC.auth(id, password)});
        },
        default: () => {
          res.status(406).send(body406);
        },
      });
    }
  }
);

router.get('/user', tokenUC.verifyJWT, async (req, res) => {
  const id = req.query.id;
  const result = await userUC.getUser(id);
  res.format({
    'application/json': () => {
      res.json(result);
    },
    default: () => {
      res.status(406).send(body406);
    },
  });
});

router.post(
  '/user',
  tokenUC.verifyJWT,
  [
    body().custom(async value => {
      return await Interceptor.validateUserJson(value);
    }),
  ],
  async (req, res, next) => {
    // validator
    const errors = await validationResult(req);
    if (!errors.isEmpty()) {
      logger.warn(errors.array());
      next(boom.badRequest(errors.array()));
    } else {
      const id = req.body.id;
      const name = req.body.name;
      const result = await userUC.postUser(id, name);
      res.format({
        'application/json': () => {
          res.json(result);
        },
        default: () => {
          res.status(406).send(body406);
        },
      });
    }
  }
);

router.get('/pdf', async (req, res) => {
  const result = await pdfUC.getPdf();
  res.send(result);
});

const body406 = {
  message: 'Not Acceptable',
};

module.exports = router;

'use strict';

const winston = require('winston');
const context = require('./context');

const winstonLogger = winston.createLogger({
  levels: {
    fatal: 0,
    error: 1,
    warn: 2,
    info: 3,
    debug: 4,
    trace: 5,
  },
  format: winston.format.combine(
    winston.format.timestamp(),
    winston.format.json()
  ),
  transports: [new winston.transports.Console()],
});

module.exports = class Logger {
  static log(...args) {
    const logger = this._getLogger();
    logger.log(args);
  }
  static fatal(...args) {
    const logger = this._getLogger();
    logger.fatal(args);
  }
  static error(...args) {
    const logger = this._getLogger();
    logger.error(args);
  }
  static warn(...args) {
    const logger = this._getLogger();
    logger.warn(args);
  }
  static info(...args) {
    const logger = this._getLogger();
    logger.info(args);
  }
  static debug(...args) {
    const logger = this._getLogger();
    logger.debug(args);
  }
  static trace(...args) {
    const logger = this._getLogger();
    logger.trace(args);
  }

  ////////////////////////
  // private functions
  ////////////////////////

  static _getLogger() {
    const logKeys = context.get('log-keys');
    const addLogs = {};
    if (logKeys) {
      for (let key of logKeys) {
        const value = context.get(key);
        if (value) {
          addLogs[key] = value;
        }
      }
      return winstonLogger.child(addLogs);
    } else {
      return winstonLogger;
    }
  }
};

